
public class AliyaPencils {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Aliya had $24 to spend.
		int aliyaMoneyStart = 24;

		// Aliya bought 7 pencils.
		int pencils = 7;

		// After buying Aliya had $10.
		int aliyaMoneyEnd = 10;

		// How much did each pencil cost?
		int pencilCost = (aliyaMoneyStart - aliyaMoneyEnd) / pencils;
		
		System.out.println("Each pencil cost $" + pencilCost);
		
		

	}

}
