
public class EvenNums {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Sum of three consecutive numbers 72.
		int total = 72;
				
		int eachNum = 72 / 3;
		int smallest = eachNum - 2;
		int largest = eachNum + 2;
		
		System.out.println("The numbers are " + smallest + ", " + eachNum + ", " + largest + ".");
		
		
		

	}

}
