
public class LengthPractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String dog = "Jif";
		int doglength = dog.length();
		System.out.println ("The dog's name has " + doglength + " characters.");
		
		System.out.println ("The dog is bad.\n No he isn't.");
		
		System.out.println ("\tIndent this line.");
		
		System.out.println ("I'm in \"a lot\" of trouble.");
		
		System.out.println ("Ding a ling.\b");
		
		System.out.println("  Peace  ".trim());
		
		System.out.println("This cat is Percy.".charAt(5));
		
		System.out.println("Percy".equalsIgnoreCase("percy"));
		
	
		

	}

}
